import React, {Component} from 'react';
import './App.css';
import './animations.scss';
import Header from './Components/Header';
import Tags from './Components/Tags';
import Cards from './Components/Cards';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {params: "home"}
  }

  componentDidMount() {
    console.log("Hello, this is params")

  }

  Child = ({match}) => {
    this.setState({params: match.params.id})
    return <h3>{match.params.id}</h3> 
  }

  Tags = () => {
      return (
          <Router>
          <div className="tags-container">
              <Link className="clover-light" to="/minimal-sunlight">Minimal Sunlight</Link>
              <Link className="clover-light" to="/moderate-sunlight">Moderate Sunlight</Link>
              <Link className="clover-light" to="/intense-sunlight">Intense Sunlight</Link>
              <Link className="clover-light" to="/less-water">Less Water</Link>
              <Link className="clover-light" to="/much-water">More Water</Link>
          </div>
          <Route path="/:id" component={this.Child} />
          </Router>
      );
  }

  render() {
    return (
      <div className="App">
        <Header />
        {Tags()}
        <Cards params={this.state.params}/>
      </div>
    );
  }

}

export default App;
