import React, {Component} from 'react';
import './App.css';
import './animations.scss';
import Header from './Components/Header';
import Cards from './Components/Cards';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {params: ""}
  }

  setStateHandler = (props) => {
    this.setState({params: props})
  }

  Child = ({match}) => {
    console.log("Params App.js: ", this.state.params)
    return match.params.id
  }

  Tags = () => {
      return (
          <Router>
          <div className="tags-container">
              <Link className="clover-light" to="/minimal-sunlight">Minimal Sunlight</Link>
              <Link className="clover-light" to="/moderate-sunlight">Moderate Sunlight</Link>
              <Link className="clover-light" to="/intense-sunlight">Intense Sunlight</Link>
              <Link className="clover-light" to="/less-water">Less Water</Link>
              <Link className="clover-light" to="/much-water">Much Water</Link>
          </div>
          <Route path="/:id" component={this.Child} onClick={setStateHandler} />
          </Router>
      );
  }

  render() {
    return (
      <div className="App">
        <Header />
        {this.Tags()}
        <Cards params={this.state.params}/>
      </div>
    );
  }

}

export default App;
